import styled from "styled-components";

function ProductCard({ product }) {
  const {
    onNavigate,
    productName,
    productNumber,
    productPrice,
    productSize,
    productRating,
    productReview,
  } = product;
  return (
    <S.Item onClick={onNavigate}>
      <h4>{productName}</h4>
      <p>Product number: {productNumber}</p>
      <p>Price: {productPrice} won</p>
      <p>Size: {productSize}</p>
      <p>Rating: {productRating}</p>
      <p>Review: {productReview}</p>
    </S.Item>
  );
}

export default ProductCard;

const Item = styled.li`
  border: 1px solid #000;
  cursor: pointer;
  width: 300px;
  margin: 16px auto;
`;

const S = {
  Item,
};
