import styled from "styled-components";

function ProductCard({ product }) {
  const { onNavigate, name, price, size, rating, review, number } = product;
  return (
    <S.Item onClick={onNavigate}>
      <h4>{product.productName}</h4>
      <p>Product number: {product.productNumber}</p>
      <p>Price: {product.productPrice} won</p>
      <p>Size: {product.productSize}</p>
      <p>Rating: {product.productRating}</p>
      <p>Review: {product.productReview}</p>
    </S.Item>
  );
}

export default ProductCard;

const Item = styled.li`
  border: 1px solid #000;
  cursor: pointer;
  width: 300px;
  margin: 16px auto;
`;

const S = {
  Item,
};
